import {
    EditOutlined,
    DeleteOutlined,
    ImageOutlined,
} from "@mui/icons-material";
import {
    Box,
    TextField,
    Divider,
    Typography,
    useTheme,
    Button,
    IconButton,
    useMediaQuery,
    MenuItem,
} from "@mui/material";
import FlexBetween from "components/FlexBetween";
import Dropzone from "react-dropzone";
import UserImage from "components/UserImage";
import WidgetWrapper from "components/WidgetWrapper";
import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setPosts } from "state";

const CreatePost = ({ picturePath }) => {
    const dispatch = useDispatch();
    const [isImage, setIsImage] = useState();
    const [image, setImage] = useState();
    const [post, setPost] = useState();
    const [selectItem, setSelectItem] = useState("");
    const { palette } = useTheme();
    const { _id } = useSelector((state) => state.user);
    const token = useSelector((state) => state.token);
    const isNonMobileScreens = useMediaQuery("(min-width: 1000px)");
    const mediumMain = palette.neutral.mediumMain;
    const medium = palette.neutral.medium;

    const handlePost = async () => {
        const formData = new FormData();
        formData.append("userId", _id);
        formData.append("description", post);
        formData.append("legalDomain", selectItem);
        if(image) {
            formData.append("picture", image);
            formData.append("picturePath", image.name);
        }
        console.log(formData);

        const response = await fetch(`http://localhost:5000/posts`, {
            method: "POST",
            headers: { Authorization: `Bearer ${token}` },
            body: formData,
        });
        const posts = await response.json();
        console.log(posts);
        dispatch(setPosts({ posts }));
        setImage(null);
        setIsImage(null);
        setPost("");
        setSelectItem("");
    };

    return(
        <WidgetWrapper>
            <FlexBetween sx={{ justifyContent: "space-between", alignItems: "flex-start", gap: "1rem" }}>
                <UserImage image={picturePath} />
                <FlexBetween gap="1.5rem" sx={{ width: "100%", flexDirection: "column", alignItems: "flex-start"}}>
                    <TextField
                        placeholder="Write your query here"
                        onChange={(e) => setPost(e.target.value)}
                        value={post}
                        sx={{
                            width: "100%",
                            backgroundColor: palette.neutral.light,
                        }}
                        multiline={true}
                    />
                    <TextField
                        select
                        size="small"
                        value={selectItem}
                        label="Choose Query Domain"
                        onChange={(e) => setSelectItem(e.target.value)}
                        sx={{
                            width: "60%",
                            backgroundColor: palette.neutral.light,
                        }}
                    >
                        <MenuItem value="Web Development">Web Development</MenuItem>
                        <MenuItem value="Machine Learning">Machine Learning</MenuItem>
                        <MenuItem value="Android / iOS Development">Android / iOS Development</MenuItem>
                        <MenuItem value="Machine Learning">Machine Learning</MenuItem>
                    </TextField>
                </FlexBetween>

            </FlexBetween>

            {isImage && (
                <Box
                    border={`1px solid ${medium}`}
                    borderRadius="5px"
                    mt="1rem"
                    p="1rem"
                >
                    <Dropzone
                        acceptedFiles=".jpg,.jpeg,.png"
                        multiple={false}
                        onDrop={(acceptedFiles) => setImage(acceptedFiles[0])}
                    >
                        {({ getRootProps, getInputProps }) => (   <FlexBetween>
                                <Box
                                    {...getRootProps()}
                                    border={`2px dashed ${palette.primary.main}`}
                                    p="1rem"
                                    sx={{ "$:hover": {cursor: "pointer"} }}
                                >
                                    <input {...getInputProps()} 
                                    />
                                    {!image ? (
                                        <p>Add Picture Here</p>
                                    ) : (
                                        <FlexBetween>
                                            <Typography>{image.name}</Typography>
                                            <EditOutlined />
                                        </FlexBetween>
                                    )}
                                </Box>
                                {image && (
                                    <IconButton
                                        onClick={() => setImage(null)}
                                        sx={{ width: "15%"}}
                                    >
                                        <DeleteOutlined />
                                    </IconButton>
                                )}
                            </FlexBetween>
                        )}
                    </Dropzone>
                </Box>
            )}

            <Divider sx={{ margin: "1.25rem 0" }} />

            <FlexBetween>
                <FlexBetween
                    gap="0.25rem"
                    onClick={() => setIsImage(!isImage)}
                >
                    <ImageOutlined sx={{ color: mediumMain }} />
                    <Typography
                        color={mediumMain}
                        sx={{ "&:hover": { cursor: "pointer", color: medium } }}
                    >
                        Image
                    </Typography>
                </FlexBetween>
                {/*isNonMobileScreens ? (
                    <>
                        <FlexBetween gap="0.25rem">
                            <GifBoxOutlined sx={{ color: mediumMain }} />
                            <Typography color={mediumMain}>Clip</Typography>
                        </FlexBetween>

                        <FlexBetween gap="0.25rem">
                            <AttachFileOutlined sx={{ color: mediumMain }} />
                            <Typography color={mediumMain}>Attachment</Typography>
                        </FlexBetween>

                        <FlexBetween gap="0.25rem">
                            <MicOutlined sx={{ color: mediumMain }} />
                            <Typography color={mediumMain}>Audio</Typography>
                        </FlexBetween>
                    </>
                    ) : (
                        <FlexBetween gap="0.25rem">
                            <MoreHorizOutlined sx={{ color: mediumMain }} />
                        </FlexBetween>
                )*/}

                    <Button
                        disabled={!post || !selectItem}
                        onClick={handlePost}
                        sx={{
                            color: palette.background.alt,
                            backgroundColor: palette.primary.main,
                            borderRadius: "3rem",
                            "&:hover": {backgroundColor: palette.primary.dark}
                        }}
                    >
                        POST
                    </Button>
            </FlexBetween>
        </WidgetWrapper>
    )
}

export default CreatePost;
